import React from 'react';
//import logo from './logo.svg';
//import './App.css';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      data: [],
      number: 0
    }
  }
  generate = () => {
    const { number } = this.state;
    if (number !== '') {
      const typeA = this.generateTypeA(parseInt(number));
      const typeB = this.generateTypeB(parseInt(number));
      const typeC = this.generateTypeC(parseInt(number));
      const typeD = this.generateTypeD(parseInt(number));

      this.setState({
        data: [typeA, typeB, typeC, typeD]
      })
    }

  }
  generateTypeA = (number) => {
    let open = number;
    let close = number;
    let result = '';
    while (open !== 0 || close !== 0) {
      if (open >= close && open !== 0) {
        result = result.concat('(')
        --open;
      } else {
        result = result.concat(')')
        --close;
      }
      if (open < -10 || close < -10)
        break;
    };
    return result;
  }
  generateTypeB = (number) => {
    let open = number;
    let close = number;
    let result = '';
    while (open > 0 || close > 0) {
      if (open !== 0) {
        result = result.concat('(')
        --open;
      } else {
        result = result.concat(')')
        --close;
      }
    }
    return result;
  }
  generateTypeC = (number) => {
    let open = number;
    let close = number;
    let result = '';
    let i = 2;
    if (number >= 2) {
      while (open > 0 || close > 0) {
        if (i > 0) {
          result = result.concat('(');
          --i;
          --open;
        } else if (i === 0) {
          result = result.concat(')')
          --close;
          --i
        } else {
          if (Math.abs(open - close) === 2 || open === 0) {
            result = result.concat(')')
            --close;

          } else {
            result = result.concat('(')
            --open;
          }
        }
      }
    } else if (number === 1) {
      result = '()';
    }
    return result;
  }
  generateTypeD = number => {
    let num = number;
    let open = 1; 
    let close = 1;
    let result = '';
    if (num > 0) {
      --num;
      console.log(open, close )
