        if (open >= close && open > 0) {
          result = result.concat('(');
          --open;
        }else{
          result = result.concat(')');
          --close;
        }
      }

      console.log('>>>>', result)
      if (num > 0)
        result = result.concat(this.generateTypeB(parseInt(num)));

      return result;
    } else {
      return result;
    }
  }
  changeState = (state, value) => {
    this.setState({
      [state]: value
    })
  }
