class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      data: 'data',
      number: ''
    }
  }

  generateTypeA = () => {
    console.log('>>>>')
    let open = 4;
    let close = 4;
    let result = '';
    while (open !== 0 || close !== 0) {
      if (open >= close && open !== 0) {
        result = result.concat('(')
        --open;
      } else {
        result = result.concat(')')
        --close;
      }
    };
    this.generateTypeB();
    this.generateTypeC();
    this.setState({
      data: result
    })
  }
  generateTypeB = () => {
    let open = 4;
    let close = 4;
    let result = '';
    while (open !== 0 || close !== 0) {
      if (open !== 0) {
        result = result.concat('(')
        --open;
      } else {
        result = result.concat(')')
        --close;
      }
    }
  }
  generateTypeC = () => {
    let open = 4;
    let close = 4;
    let result = '';
    let i = 2;
    while (open !== 0 || close !== 0) {
      if (i > 0) {
        result = result.concat('(');
        --i;
        --open;
      } else if (i === 0) {
        result = result.concat(')')
        --close;
        --i
      } else {
        if (Math.abs(open - close) === 2 || open === 0) {
          result = result.concat(')')
          --close;

        } else {
          result = result.concat('(')
          --open;
        }
      }
    }
    console.log('generateTypeC', result)
  }
  changeState = (state, value) => {
    this.setState({
      [state]: value
    })
  }
  render() {
    const { number } = this.state;
    return (
