      //       Learn React
      //   </p>
      // </div>
      <div className='App'>
        <h1>
          result
       </h1>
        <h3>
          {this.state.data}
        </h3>
        <input value={number} type='tel' onChange={e => this.changeState('number', e.target.value)}>
        </input>
        <button onClick={this.generateTypeA}>
          QQ
       </button>
      </div>
    );
  }
}

export default App;
